      //let rObj = {}
      //rObj[word.question] = word.answer
    //   return ( 
    //       <div>
    //         <h1>{word.question}</h1>
    //         <p>{word.answer}</p>
    //       </div>
    //   );
    return (

    <div key={words.indexOf(word)}>
       <div className="app" key={word.question} onClick={ word.toggleCard }> 
