  );

  return (
    // <>
    //   <div className="app">
    //     <h1>Hi there!</h1>
    //   </div>
    //   {showDiv && 
    //     <div className="app">
    //       <p>Conditionally render div here</p>
    //     </div>}
    //   <Button onClick={toggleAnswer} >{ !showDiv ? 'Show Answer' : 'Hide Answer' }</Button>
    // </>
