import React, { useState } from 'react';
import Button from './Button/Button';

import '../../App.css';

function CardGroup() {
  const [showDiv, setShowDiv] = useState(false);

  const toggleAnswer = () => {
    setShowDiv(prevState => !prevState)
  }

  const words = [
      {
        question: 'Are',
        answer: 'no'
      },
      {
        question: 'we',
        answer: 'no'
      },
      {
        question: 'There',
        answer: 'no'
      },
      {
        question: 'yet',
        answer: 'no'
      }
  ]

  const cardWordGroup = words.map(({word}) => {
    return 
        <div className="app">
            <h1>{word.question}</h1>
        </div>
        {showDiv && 
        <div className="app">
            <p>Conditionally render div here</p>
            {word.answer}
        </div>}
        <Button onClick={toggleAnswer} >{ !showDiv ? 'Show Answer' : 'Hide Answer' }</Button>

  });

  return (
    // <>
    //   <div className="app">
    //     <h1>Hi there!</h1>
    //   </div>
    //   {showDiv && 
    //     <div className="app">
    //       <p>Conditionally render div here</p>
    //     </div>}
    //   <Button onClick={toggleAnswer} >{ !showDiv ? 'Show Answer' : 'Hide Answer' }</Button>
    // </>
    {cardWordGroup}
  );
}

export default CardGroup;
